mapper.select:
SELECT id, request_id, trace_id, parent_id, server_key, server_name, load_balance_strategy, method, path, mcp_method, tool_name, query_params, request_headers, request_body, request_size, response_status, response_headers, response_body, response_size, start_time, end_time, duration, queue_time, connect_time, process_time, client_id, client_ip, user_agent, session_id, is_success, is_cached, is_retry, retry_count, error_message, error_code, error_type, metadata, tags, created_at FROM routing_logs WHERE server_key = #{targetServer}
AND start_time BETWEEN #{startTime} AND #{endTime}
ORDER BY start_time DESC
LIMIT #{limit}

SELECT id, request_id, trace_id, parent_id, server_key, server_name, load_balance_strategy, method, path, mcp_method, tool_name, query_params, request_headers, request_body, request_size, response_status, response_headers, response_body, response_size, start_time, end_time, duration, queue_time, connect_time, process_time, client_id, client_ip, user_agent, session_id, is_success, is_cached, is_retry, retry_count, error_message, error_code, error_type, metadata, tags, created_at FROM routing_logs WHERE is_success = FALSE
AND start_time BETWEEN #{startTime} AND #{endTime}
ORDER BY start_time DESC
LIMIT #{limit}

SELECT COUNT(*) 
FROM routing_logs
WHERE start_time BETWEEN #{startTime} AND #{endTime}

